#Import all needed libraries
import math
import numpy as np

#Buffer variables
teta_2 = []                     #diffraction angle (2θ)
h = []                          #miller index in x-axis
k = []                          #miller index in y-axis
l = []                          #miller index in z-axis
teta = []                       #diffraction angle (θ)
sin_teta = []                   #sin θ
sin_2_teta = []                 #sin 2θ
sin_squared_teta = []           #(sin θ)^2
sin_squared_2_teta = []         #(sin 2θ)^2
sigma = []                      #σ (depends on structure)
tau = []                        #τ (depends on structure)
phi = []                        #φ (depends on structure)
chi = []                        #χ (depends on structure)
psi = []                        #ψ (depends on structure)
sigma_sigma_squared = []        #Σ(σ^2)
sigma_tau_squared = []          #Σ(τ^2)
sigma_phi_squared = []          #Σ(φ^2)
sigma_chi_squared = []          #Σ(χ^2)
sigma_psi_squared = []          #Σ(φ^2)
sigma_sigma_tau = []            #Σστ
sigma_sigma_phi = []            #Σσφ
sigma_sigma_chi = []            #Σσχ
sigma_sigma_psi = []            #Σσψ
sigma_tau_phi = []              #Στφ
sigma_tau_chi = []              #Στχ
sigma_tau_psi = []              #Στψ
sigma_phi_chi = []              #Σφχ
sigma_phi_psi = []              #Σφψ
sigma_chi_psi = []              #Σχψ
sigma_sigma_sin_squared = []    #Σσ(sin θ)^2
sigma_tau_sin_squared = []      #Στ(sin θ)^2
sigma_phi_sin_squared = []      #Σφ(sin θ)^2
sigma_chi_sin_squared = []      #Σχ(sin θ)^2
sigma_psi_sin_squared = []      #Σψ(sin θ)^2

#This function grant input procedure from users.
def datainput(n_row):
  """
  This function is used for generate input data from user.
  Number of rows from XRD data is defined as n_row parameter.
  Data from a row stored as a string and splitted by space to a list.
  """

  print("""Please input the data from each row according to this format:
\"2θ(space)h(space)k(space)l\".
Example: 33.450 2 2 0\n""")
  counter = 1
  while counter <= n_row:
    if counter < 10:
      if counter == 1:
        x = input("The " + str(counter) + "st row: ").split(" ")
      elif counter == 2:
        x = input("The " + str(counter) + "nd row: ").split(" ")
      elif counter == 3:
        x = input("The " + str(counter) + "rd row: ").split(" ")
      else:
        x = input("The " + str(counter) + "th row: ").split(" ")
    else:
      if str(counter)[-2] == "1":
        x = input("The " + str(counter) + "th row: ").split(" ")
      else:
        if str(counter)[-1] == "1":
          x = input("The " + str(counter) + "st row: ").split(" ")
        elif str(counter)[-1] == "2":
          x = input("The " + str(counter) + "nd row: ").split(" ")
        elif str(counter)[-1] == "3":
          x = input("The " + str(counter) + "rd row: ").split(" ")
        else:
          x = input("The " + str(counter) + "th row: ").split(" ")

    counter += 1
    if len(x) == 4:
      i = 0
      while i == 0:
        i = 1
        try:
          teta_2.append(float(x[0]))
          h.append(int(x[1]))
          k.append(int(x[2]))
          l.append(int(x[3]))
        except ValueError:
          counter -= 1
          x = []
          print("ERROR!! Please use \".\" instead \",\"")
          continue
    else:
      counter -= 1
      x = []
      print("ERROR!! Please input all data needed.")

#This function process input about angle given by users
def angle(n_row):
  """
  This function is used for preprocess angle
  from data of teta_2 generated from user before.
  """

  #teta (θ)
  for i in range(n_row):
    a = float(teta_2[i]/2)
    teta.append(a)

  #sinus teta (sin θ)
  for i in range(n_row):
    a = math.sin(math.radians(teta[i]))
    sin_teta.append(a)

  #sinus 2 teta (sin 2θ)
  for i in range(n_row):
    a = math.sin(math.radians(teta_2[i]))
    sin_2_teta.append(a)

  #sinus teta squared [(sin θ)^2]
  for i in range(n_row):
    a = sin_teta[i]*sin_teta[i]
    sin_squared_teta.append(a)
    
  #sinus 2 teta squared [(sin 2θ)^2]
  for i in range(n_row):
    a = sin_2_teta[i]*sin_2_teta[i]
    sin_squared_2_teta.append(a)

#This function grant summation procedure from all element of a list.
def summation(thelist):
  result = 0
  for i in thelist:
    result += i
  return result

#Function for defining matrix component for matrix equation
def matrixcomponent(n):
  """
  This function is used for count each component for matrix equation.
  """

  #sum of sigma squared (Σ(σ^2))
  for i in range(n):
    a = sigma[i]*sigma[i]
    sigma_sigma_squared.append(a)
  global sss
  sss = summation(sigma_sigma_squared)

  #sum of tau squared (Σ(τ^2))
  for i in range(n):
    a = tau[i]*tau[i]
    sigma_tau_squared.append(a)
  global sts
  sts = summation(sigma_tau_squared)

  #sum of phi squared (Σ(φ^2))
  for i in range(n):
    a = phi[i]*phi[i]
    sigma_phi_squared.append(a)
  global sphis
  sphis = summation(sigma_phi_squared)

  #sum of chi squared Σ(χ^2)
  for i in range(n):
    a = chi[i]*chi[i]
    sigma_chi_squared.append(a)
  global scs
  scs = summation(sigma_chi_squared)

  #sum of psi squared Σ(ψ^2)
  for i in range(n):
    a = psi[i]*psi[i]
    sigma_psi_squared.append(a)
  global sps
  sps = summation(sigma_psi_squared)

  #sum of sigma times tau (Σστ)
  for i in range(n):
    a = sigma[i]*tau[i]
    sigma_sigma_tau.append(a)
  global sst
  sst = summation(sigma_sigma_tau)

  #sum of sigma times phi (Σσφ)
  for i in range(n):
    a = sigma[i]*phi[i]
    sigma_sigma_phi.append(a)
  global ssphi
  ssphi = summation(sigma_sigma_phi)

  #sum of sigma times chi (Σσχ)
  for i in range(n):
    a = sigma[i]*chi[i]
    sigma_sigma_chi.append(a)
  global ssc
  ssc = summation(sigma_sigma_chi)

  #sum of sigma times psi (Σσψ)
  for i in range(n):
    a = sigma[i]*psi[i]
    sigma_sigma_psi.append(a)
  global ssp
  ssp = summation(sigma_sigma_psi)

  #sum of tau times phi (Στφ)
  for i in range(n):
    a = tau[i]*phi[i]
    sigma_tau_phi.append(a)
  global stphi
  stphi = summation(sigma_tau_phi)

  #sum of tau times chi (Στχ)
  for i in range(n):
    a = tau[i]*chi[i]
    sigma_tau_chi.append(a)
  global stc
  stc = summation(sigma_tau_chi)
  
  #sum of tau times psi (Στψ)
  for i in range(n):
    a = tau[i]*psi[i]
    sigma_tau_psi.append(a)
  global stp
  stp = summation(sigma_tau_psi)

  #sum of phi times chi (Σφχ)
  for i in range(n):
    a = phi[i]*chi[i]
    sigma_phi_chi.append(a)
  global sphic
  sphic = summation(sigma_phi_chi)

  #sum of phi times psi (Σφψ)
  for i in range(n):
    a = phi[i]*psi[i]
    sigma_phi_psi.append(a)
  global sphip
  sphip = summation(sigma_phi_psi)

  #sum of chi times psi (Σχψ)
  for i in range(n):
    a = chi[i]*psi[i]
    sigma_chi_psi.append(a)
  global scp
  scp = summation(sigma_chi_psi)

  #sum of sigma times squared of sinus teta (Σσ*(sin θ)^2)
  for i in range(n):
    a = sigma[i]*sin_squared_teta[i]
    sigma_sigma_sin_squared.append(a)
  global ssss
  ssss = summation(sigma_sigma_sin_squared)

  #sum of tau times squared of sinus teta (Στ*(sin θ)^2)
  for i in range(n):
    a = tau[i]*sin_squared_teta[i]
    sigma_tau_sin_squared.append(a)
  global stss
  stss = summation(sigma_tau_sin_squared)

  #sum of phi times squared of sinus teta (Σφ*(sin θ)^2)
  for i in range(n):
    a = phi[i]*sin_squared_teta[i]
    sigma_phi_sin_squared.append(a)
  global sphiss
  sphiss = summation(sigma_phi_sin_squared)

  #sum of chi times squared of sinus teta (Σχ*(sin θ)^2)
  for i in range(n):
    a = chi[i]*sin_squared_teta[i]
    sigma_chi_sin_squared.append(a)
  global scss
  scss = summation(sigma_chi_sin_squared)

  #sum of psi times squared of sinus teta (Σψ*(sin θ)^2)
  for i in range(n):
    a = psi[i]*sin_squared_teta[i]
    sigma_psi_sin_squared.append(a)
  global spss
  spss = summation(sigma_psi_sin_squared)

