#Function for calculating CUBIC structure's parameter.
def cubic(n_row):
  #Insert and process the input
  datainput(n_row)
  angle(n_row)

  #sigma for cubic structure calculation (σ) -> no sigma was defined
  for i in range(n_row):
    a = 0
    sigma.append(a)

  #tau for cubic structure calculation (τ) -> no tau was defined 
  for i in range(n_row):
    a = 0
    tau.append(a)

  #phi for cubic structure calculation (φ) ->  no phi was defined
  for i in range(n_row):
    a = 0
    phi.append(a)

  #chi for cubic structure calculation (χ = h^2 + k^2 + l^2)
  for i in range(n_row):
    a = (h[i]*h[i])+(k[i]*k[i])+(l[i]*l[i])
    chi.append(a)
    
  #delta for cubic structure calculation (ψ = 10 * (sin 2θ)^2)
  for i in range(n_row):
    a = 10*sin_squared_2_teta[i]
    psi.append(a)

  #Define the matrix equation
  matrixcomponent(n_row)

  #Solving the equation above in form of matrix equation using Cramer-Cohen method
  right_matrix = np.array([(scs,scp),
                            (scp,sps)])
  left_matrix = np.array([(scss),
                           (spss)])
  result = np.linalg.solve(right_matrix, left_matrix)            #S, T
  
  #Asking for wavelength used in the data (λ)
  lam = float(input("Wavelength used (in Angstrom) = "))
  
  #Lattice parameter (a = λ/2*sqrt(S))
  a = lam/(2*math.sqrt(result[0]))
  
  print("\nLattice parameter from this cubic crystal structure (in Angstrom) is a = %.3f with α = β = γ = 90 degrees." %a)

