#Function for calculating HEXAGONAL structure's parameter.
def hexagonal(n_row):
  #Insert and process the input
  datainput(n_row)
  angle(n_row)

  #sigma for hexagonal structure calculation (σ) -> no sigma was defined
  for i in range(n_row):
    a = 0
    sigma.append(a)

  #tau for hexagonal structure calculation (τ) -> no tau was defined
  for i in range(n_row):
    a = 0
    tau.append(a)

  #phi for hexagonal structure calculation (φ = h^2 + hk + k^2)
  for i in range(n_row):
    a = h[i]*h[i]+h[i]*k[i]+k[i]*k[i]
    phi.append(a)

  #chi for hexagonal structure calculation (χ = l^2)
  for i in range(n_row):
    a = l[i]*l[i]
    chi.append(a)

  #psi for hexagonal structure calculation (ψ = 10 * (sin 2θ)^2)
  for i in range(n_row):
    a = 10*sin_squared_2_teta[i]
    psi.append(a)

  #Define the matrix equation
  matrixcomponent(n_row)

  #Solving the equation above in form of matrix equation using Cramer-Cohen method
  right_matrix = np.array([(sphis,sphic,sphip),
                            (sphic,scs,scp),
                            (sphip,scp,sps)])
  left_matrix = np.array([(sphiss),
                           (scss),
                           (spss)])
  result = np.linalg.solve(right_matrix, left_matrix)            #R, S, T

  #Asking for wavelength used in the data (λ)
  lam = float(input("Wavelength used (in Angstrom) = "))

  #Lattice parameter
  #a = λ/sqrt(3*R)
  a = lam/math.sqrt(3*result[0])
  #c = λ/2*sqrt(S)
  c = lam/(2*math.sqrt(result[1]))
  
  print("\nLattice parameter from this hexagonal crystal structure (in Angstrom) is a = %.3f and c = %.3f with α = β = 90 degrees and γ = 120 degrees." %(a,c))

