#Function for calculating ORTHORHOMBIC structure's parameter.
def orthorhombic(n_row):
  #Insert and process the input
  datainput(n_row)
  angle(n_row)
  
  #sigma for orthorhombic structure calculation (σ) -> no sigma was defined
  for i in range(n_row):
    a = 0
    sigma.append(a)
    
  #tau for orthorhombic structure calculation (τ = h^2)
  for i in range(n_row):
    a = h[i]*h[i]
    tau.append(a)

  #phi for orthorhombic structure calculation (φ = k^2)
  for i in range(n_row):
    a = k[i]*k[i]
    phi.append(a)

  #chi for orthorhombic structure calculation (χ = l^2)
  for i in range(n_row):
    a = l[i]*l[i]
    chi.append(a)

  #psi for orthorhombic structure calculation (ψ = 10 * (sin 2θ)^2)
  for i in range(n_row):
    a = 10*sin_squared_2_teta[i]
    psi.append(a)

  #Define the matrix equation
  matrixcomponent(n_row)
    
  #Solving the equation above in form of matrix equation using Cramer-Cohen method
  right_matrix = np.array([(sts,stphi,stc,stp),
                            (stphi,sphis,sphic,sphip),
                            (stc,sphic,scs,scp),
                            (stp,sphip,scp,sps)])
  left_matrix = np.array([(stss),
                           (sphiss),
                           (scss),
                           (spss)])
  result = np.linalg.solve(right_matrix, left_matrix)                  #Q, R, S, T
  
  #Asking for wavelength used in the data (λ)
  lam = float(input("Wavelength used (in Angstrom) = "))

  #Lattice parameter
  #a = λ/2*sqrt(Q)
  a = lam/(2*math.sqrt(result[0]))
  #b = λ/2*sqrt(R)
  b = lam/(2*math.sqrt(result[1]))
  #c = λ/2*sqrt(S)
