#Function for calculating MONOCLINIC structure's parameter.
def monoclinic(n_row):
  #Insert and process the input
  beta_angle = float(input("beta_angle: "))   #According to XRD data
  datainput(n_row)
  angle(n_row)
  
  #sigma for monoclinic structure calculation (σ = h^2 / (sin betaangle)^2)
  for i in range(n_row):
    a = (h[i]*h[i])/(math.sin(math.radians(beta_angle))*math.sin(math.radians(beta_angle)))
    sigma.append(a)

  #tau for monoclinic structure calculation (τ = k^2)
  for i in range(n_row):
    a = k[i]*k[i]
    tau.append(a)

  #phi for monoclinic structure calculation (φ = l^2 / (sin betaangle)^2)
  for i in range(n_row):
    a = (l[i]*l[i])/(math.sin(math.radians(beta_angle))*math.sin(math.radians(beta_angle)))
    phi.append(a)

  #chi for monoclinic structure calculation (χ = hl*cos betaangle / (sin betaangle)^2))
  for i in range(n_row):
    a = (h[i]*l[i]*math.cos(math.radians(beta_angle)))/(math.sin(math.radians(beta_angle))*math.sin(math.radians(beta_angle)))
    chi.append(a)
  
  #psi for monoclinic structure calculation (ψ = 10 * (sin 2θ)^2)
  for i in range(n_row):
    a = 10*sin_squared_2_teta[i]
    psi.append(a)

  #Define the matrix equation
  matrixcomponent(n_row)
   
  #Solving the equation above in form of matrix equation using Cramer-Cohen method
  right_matrix = np.array([(sss,sst,ssphi,ssc,ssp),
                            (sst,sts,stphi,stc,stp),
                            (ssphi,stphi,sphis,sphic,sphip),
                            (ssc,stc,sphic,scs,scp),
                            (ssp,stp,sphip,scp,sps)])
  left_matrix = np.array([(ssss),
                           (stss),
                           (sphiss),
                           (scss),
                           (spss)])
  result = np.linalg.solve(right_matrix, left_matrix)                #P, Q, R, S, T
  
  #Asking for wavelength used in the data (λ)
  lam = float(input("Wavelength used (in Angstrom) = "))

  #Lattice parameter
  #a = λ/2*sqrt(P)
  a = lam/(2*math.sqrt(result[0]))
  #b = λ/2*sqrt(Q)
  b = lam/(2*math.sqrt(result[1]))
  #c = λ/2*sqrt(R)
  c = lam/(2*math.sqrt(result[2]))

  print("\nLattice parameter from this monoclinic crystal structure (in Angstrom) is a = %.3f, b = %.3f and c = %.3f with α = γ = 90 degrees and β = %.3f degrees." %(a,b,c,beta_angle))

